% -------------------------------------------------------------------
%  Generated by MATLAB on 3-May-2015 13:59:17
%  计算（-8）的1/3次方
%  《matlab教程》 P10例1.3_6
% -------------------------------------------------------------------

%1.用求幂符号计算，只能得到第一象限的方根
a = -8;
r_a=a^(1/3)
%2.利用专门命令nthroot可求实根
r_n=nthroot(a,3)   
%3.先构造多项式p(r)=r^3-a
p=[1,0,0,-a]; %p是多项式p(r)的系数向量；（命令末尾的“英文状态分号”使该命令执行后，不显示结果）
R=roots(p)   %求p(r)=r^3-a=0的根
%4.图形表示
MR=abs(R(1));  %计算复数的模
t=0:pi/20:2*pi;   %产生参变量在0到2*pi间的一组采样点
x=MR*sin(t);
y=MR*cos(t);
plot(x,y,'b:'),grid on  %画一个半径为R的圆；（逗号的作用：分隔符；既可用于分隔指令；也可用于分隔数组列）
hold on
plot(R(2),'.','MarkerSize',30,'Color','r')  %画第一象限的方根
plot(R([1,3]),'o','MarkerSize',15,'Color','b')  %画另两个方根
axis([-3,3,-3,3]),axis square   %保证屏幕显示呈真圆
hold off
